	for (i = 0; i < sizeof (digest); ++i) {
		digest[i] = i * 3;
		digest2[i] = i * 2;
		measurement[i] = 50 + i;
	}

	for (i = 0; i < (ECC_KEY_LENGTH_256 * 2); ++i) {
		signature[i] = i * 10;
	}

