	uint8_t sig_der[ECC_DER_P256_ECDSA_MAX_LENGTH];
	uint8_t signature2[ECC_KEY_LENGTH_256 * 2];
	uint8_t sig_der2[ECC_DER_P256_ECDSA_MAX_LENGTH];
