	status |= mock_expect (&testing.secondary_hash.mock, testing.secondary_hash.base.update,
		&testing.secondary_hash, 0, MOCK_ARG_PTR_CONTAINS (digest, sizeof (digest)),
		MOCK_ARG (sizeof (digest)));
