	data.bitmask = NULL;
	data.bitmask_length = 0;
	data.check = CFM_CHECK_EQUAL;
	data.big_endian = false;
	data.data_count = 1;
	data.allowable_data[0].data_len = sizeof (measurement2);
	data.allowable_data[0].data = measurement2;
