	for (i = 0; i < SPDM_NONCE_LEN; ++i, ++offset) {
		rsp_buf[offset] = SPDM_NONCE_LEN - i;
	}

	opaque_length = (uint16_t*) &rsp_buf[offset];
	*opaque_length = 5;
	offset += 2;

	for (i = 0; i < *opaque_length; ++i, ++offset) {
		rsp_buf[offset] = 5 + i;
	}

	for (i = 0; i < 5; ++i) {
		rsp_buf[offset + i] = i * 10;
	}

